INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Italiana", "Massas, pizzas e molhos saborosos. Ingredientes frescos como azeite de oliva, tomates, queijos e ervas aromáticas. Tradição e autenticidade em cada prato.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Japonesa", "Sushi, sashimi e tempura. Peixes frescos, arroz temperado e ingredientes delicados. Uma experiência culinária refinada.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Brasileira", "Feijoada, churrasco e açaí. Uma mistura de sabores regionais e influências culturais. Alegria e diversidade à mesa.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Mediterrânea", "Azeite de oliva, vegetais frescos e grãos integrais. Uma dieta saudável e balanceada inspirada na região do Mediterrâneo.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Indiana", "Curry, masalas e naan. Uma explosão de especiarias e sabores exóticos. Uma jornada pela rica cultura culinária indiana.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Mexicana", "Tacos, guacamole e pimentas. Cores vibrantes e sabores picantes que refletem a paixão e a energia do México.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Francesa", "Croissants, queijos refinados e molhos elegantes. Sofisticação e tradição da alta gastronomia francesa.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Chinesa", "Frango xadrez, arroz frito e dim sum. Uma combinação de sabores agridoces e texturas variadas.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Tailandesa", "Pad Thai, curry tailandês e coco. Equilíbrio entre doce, picante, azedo e salgado.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Grega", "Moussaka, azeitonas e queijos feta. Ingredientes frescos e influências mediterrâneas.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Turca", "Kebabs, baklava e pão pita. Uma fusão de sabores do Oriente Médio e Mediterrâneo.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Alemã", "Salsichas, pretzels e chucrute. Comida substancial e saborosa com raízes germânicas.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Coreana", "Kimchi, bulgogi e bibimbap. Fermentação e intensidade de sabores caracterizam essa culinária.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Peruana", "Ceviche, papa a la huancaina e rocoto relleno. Mistura única de ingredientes andinos e influências espanholas.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Árabe", "Hummus, falafel e shawarma. Especiarias e pratos tradicionais do Oriente Médio.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Italiana do Sul", "Lasanha, cannoli e pizza napolitana. A cozinha das regiões ensolaradas do sul da Itália.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Espanhola", "Paella, tapas e gazpacho. Sabores vibrantes e influências das diversas regiões da Espanha.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Vietnamita", "Pho, spring rolls e banh mi. Cozinha fresca e leve do Sudeste Asiático.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Russa", "Borscht, pelmeni e blini. Pratos de conforto e ingredientes tradicionais russos.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Argentino", "Asado, empanadas e chimichurri. A carne de qualidade é o destaque da culinária argentina.", utc_timestamp, utc_timestamp);
INSERT INTO kitchen (name, description, created_at, updated_at) VALUES ("Marroquina", "Couscous, tajine e pastilla. Sabores exóticos e mistura de especiarias.", utc_timestamp, utc_timestamp);
